import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3.5vmax",
    position: "relative",
    bottom: "70px",
    marginBottom: "3rem"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30,
    marginTop: 50
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "3vmax",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  about: {
    fontFamily: "Righteous"
  }
};

class ApodResult extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.all}>
          <div className={classes.body}>
            <div>
              <h1 className={classes.header}>
                {this.props.receivedInfo.title}
              </h1>
              <h3 className={classes.info}>
                Date: {this.props.receivedInfo.date}
              </h3>
              <h3 className={classes.info}>{this.props.receivedInfo.artist}</h3>
            </div>
            <div className={classes.grid}>
              <img
                className={classes.picture}
                src={this.props.receivedInfo.link}
                alt=""
              />
              <div className={classes.text}>
                <h3 className={classes.about}>About the Work</h3>
                <p>{this.props.receivedInfo.explanation}</p>
              </div>
            </div>
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              style={{ marginBottom: 70 }}
              href="/"
            >
              Go Back
            </Button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(ApodResult);
