import React, { Component } from "react";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  header: {
    fontFamily: "Righteous",
    color: "#d8dcf2",
    height: 80,
    fontSize: 100,
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    width: 300,
    marginBottom: 50
  },
  button: {
    marginTop: 70
  }
});

class Contact extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    let value = event.target.value;
    if (event.target.name === "name") {
      this.props.dispatch(nameUpdate(value));
    }
    if (event.target.name === "email") {
      this.props.dispatch(emailUpdate(value));
    }
    if (event.target.name === "message") {
      this.props.dispatch(messageUpdate(value));
    }
  }

  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Animated
          animationIn="fadeInRight"
          animationOut="fadeOut"
          isVisible={true}
        >
          <h1 className={classes.header}>Contact</h1>
          <p className={classes.helper}>We'd like to hear from you!</p>
        </Animated>
        <div className={classes.root}>
          <FormControl required className={classes.form}>
            <TextField
              required
              name="name"
              label="Name"
              value={this.props.name}
              onChange={this.handleChange}
            />
          </FormControl>
          <FormControl required className={classes.form}>
            <TextField
              required
              name="email"
              label="Email"
              value={this.props.email}
              onChange={this.handleChange}
            />
          </FormControl>
          <FormControl required className={classes.form}>
            <TextField
              required
              name="message"
              label="Message"
              multiline="true"
              rows={5}
              value={this.props.message}
              onChange={this.handleChange}
            />
            <Button
              variant="contained"
              color="secondary"
              className={classes.button}
              type="submit"
            >
              Submit
            </Button>
          </FormControl>
        </div>
      </React.Fragment>
    );
  }
}

export default withStyles(styles)(Contact);
