import React, { Component } from "react";
import { connect } from "react-redux";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import "./main.css";
import { getPicture } from "../actions/formActions/actions";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";

const styles = {
  body: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr 1fr",
    gridColumnGap: 50,
    justifyItems: "center",
    position: "relative",
    bottom: 100
  },
  card: {
    maxWidth: 300,
    maxHeight: 250
  },
  media: {
    objectFit: "cover"
  },
  cardText: {
    color: "#555761"
  }
};

class Main extends Component {
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <div className="root">
          <Toolbar>
            <Button id="buttons" href="about">
              About
            </Button>
            <Button id="buttons" href="contact">
              Contact
            </Button>
          </Toolbar>
        </div>

        <div className="showCase">
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <h1>Universa Localis</h1>
          </Animated>
          <p>The whole universe on your screen</p>
        </div>
        <img
          className="icons"
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/e/e5/NASA_logo.svg/2000px-NASA_logo.svg.png"
          alt="Nasa"
        />
        <div className={classes.body}>
          <Card className={classes.card}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                className={classes.media}
                height="140"
                image="https://apod.nasa.gov/apod/image/1812/GeminidAurora_Hansen1_960.jpg"
                title="Astronomy Picture"
                onClick={() => this.props.dispatch(getPicture())}
              />
              <CardContent className={classes.cardText}>
                <Typography gutterBottom variant="h5" component="h2">
                  APOD
                </Typography>
                <Typography component="p">
                  Get the astronomy picture of the day, everyday!
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>

          <Card className={classes.card}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                className={classes.media}
                height="140"
                image="https://apod.nasa.gov/apod/image/1812/GeminidAurora_Hansen1_960.jpg"
                title="Astronomy Picture"
                onClick={() => this.props.dispatch(getPicture())}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  APOD
                </Typography>
                <Typography component="p">
                  Get the astronomy picture of the day, everyday!
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              <Button size="small" color="primary">
                Share
              </Button>
              <Button size="small" color="primary">
                Learn More
              </Button>
            </CardActions>
          </Card>

          <Card className={classes.card}>
            <CardActionArea>
              <CardMedia
                component="img"
                alt=""
                className={classes.media}
                height="140"
                image="https://apod.nasa.gov/apod/image/1812/GeminidAurora_Hansen1_960.jpg"
                title="Astronomy Picture"
                onClick={() => this.props.dispatch(getPicture())}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  APOD
                </Typography>
                <Typography component="p">
                  Get the astronomy picture of the day, everyday!
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              <Button size="small" color="primary">
                Share
              </Button>
              <Button size="small" color="primary">
                Learn More
              </Button>
            </CardActions>
          </Card>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    astro_picture: state.astro_picture
  };
};

Main.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(Main);
const mainConnected = connect(mapStateToProps)(withstyles);
export default mainConnected;
