import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import Input from "@material-ui/core/Input";

import {
  nameUpdate,
  emailUpdate,
  messageUpdate,
  fetchStart
} from "../actions/contactActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Righteous",
    textDecoration: "underline",
    textDecorationColor: "#e10b4f",
    textAlign: "left",
    marginLeft: 20
  },
  contact: {
    position: "relative",
    bottom: 60
  },
  header: {
    fontFamily: "Righteous",
    color: "#d8dcf2",
    height: 80,
    fontSize: 100,
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    width: 300,
    marginBottom: 40
  },
  button: {
    marginTop: 50
  }
});

class Contact extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    let value = event.target.value;
    if (event.target.name === "name") {
      this.props.dispatch(nameUpdate(value));
    }
    if (event.target.name === "email") {
      this.props.dispatch(emailUpdate(value));
    }
    if (event.target.name === "message") {
      this.props.dispatch(messageUpdate(value));
    }
  }

  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Museum Locale
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.contact}>
          <h1 className={classes.header}>Contact</h1>
          <p className={classes.helper}>We'd like to hear from you!</p>
          <div className={classes.root}>
            <FormControl>
              <Input required className={classes.form}>
                <TextField
                  required
                  name="name"
                  label="Name"
                  value={this.props.name}
                  onChange={this.handleChange}
                />
              </Input>
              <Input required className={classes.form}>
                <TextField
                  required
                  name="email"
                  label="Email"
                  value={this.props.email}
                  onChange={this.handleChange}
                />
              </Input>
              <Input required className={classes.form}>
                <TextField
                  required
                  name="message"
                  label="Message"
                  multiline="true"
                  rows={5}
                  value={this.props.message}
                  onChange={this.handleChange}
                />
              </Input>
              <Button
                variant="contained"
                color="secondary"
                className={classes.button}
                type="submit"
                onClick={() => this.props.dispatch(fetchStart())}
              >
                Submit
              </Button>
            </FormControl>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    name: state.form.name,
    email: state.form.email,
    message: state.form.message
  };
};

Contact.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(Contact);
const contactConnected = connect(mapStateToProps)(withstyles);
export default contactConnected;
