import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Toolbar from "@material-ui/core/Toolbar";
import {
  nameUpdate,
  emailUpdate,
  messageUpdate,
  validateForm
} from "../actions/contactActions/actions";
import { Animated } from "react-animated-css";
import { withStyles } from "@material-ui/core/styles";
import FormControl from "@material-ui/core/FormControl";
import FormHelperText from "@material-ui/core/FormHelperText";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const styles = theme => ({
  root: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr 1fr",
    backgroundColor: "rgba(200, 200, 200, .5)"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Righteous",
    textDecoration: "underline",
    textDecorationColor: "#440be1",
    textAlign: "left",
    marginLeft: 20
  },
  contact: {
    position: "relative",
    bottom: 60
  },
  header: {
    fontFamily: "Righteous",
    color: "#d8dcf2",
    height: 80,
    fontSize: 100,
    marginTop: 50
  },
  helper: {
    color: "#d8dcf2",
    fontSize: "1.5rem",
    fontFamily: "Quicksand",
    position: "relative",
    bottom: 40
  },
  form: {
    gridColumnStart: 2,
    gridColumnEnd: 3,
    width: 300,
    marginBottom: 20
  },
  button: {
    marginTop: 20
  }
});

class Contact extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    let value = event.target.value;
    if (event.target.name === "name") {
      this.props.dispatch(nameUpdate(value));
    }
    if (event.target.name === "email") {
      this.props.dispatch(emailUpdate(value));
    }
    if (event.target.name === "message") {
      this.props.dispatch(messageUpdate(value));
    }
  }

  handleSubmit() {
    this.props.dispatch(validateForm());
  }

  render() {
    const { classes } = this.props;
    let showNameError;
    let showEmailError;
    let showMessageError;

    if (this.props.errorFound) {
      this.props.errorArray.forEach(error => {
        if (error === 1) {
          showNameError = "Name field can't be empty!";
        } else if (error === 2) {
          showEmailError = "Email address is not valid!";
        } else if (error === 3) {
          showEmailError = "Email field can't be empty! ";
        } else if (error === 4) {
          showMessageError = "Message field can't be empty!";
        }
      });
    }

    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.contact}>
          <h1 className={classes.header}>Contact</h1>
          <p className={classes.helper}>We'd like to hear from you!</p>
          <div className={classes.root}>
            <FormControl required className={classes.form}>
              <TextField
                required
                name="name"
                label="Name"
                value={this.props.name}
                onChange={this.handleChange}
              />
              <FormHelperText error>{showNameError}</FormHelperText>
            </FormControl>
            <FormControl required className={classes.form}>
              <TextField
                required
                className={classes.field}
                name="email"
                label="Email"
                value={this.props.email}
                onChange={this.handleChange}
              />
              <FormHelperText error>{showEmailError}</FormHelperText>
            </FormControl>
            <FormControl required className={classes.form}>
              <TextField
                required
                name="message"
                label="Message"
                multiline="true"
                rows={5}
                value={this.props.message}
                onChange={this.handleChange}
              />
              <FormHelperText error>{showMessageError}</FormHelperText>
            </FormControl>
          </div>
          <Button
            variant="contained"
            color="secondary"
            className={classes.button}
            type="submit"
            onClick={this.handleSubmit}
          >
            Submit
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.ContactReducer;
  return {
    name: state.form.name,
    email: state.form.email,
    message: state.form.message,
    errorArray: state.errorArray
  };
};

Contact.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(Contact);
const contactConnected = connect(mapStateToProps)(withstyles);
export default contactConnected;
