import {
  SUBMITTED,
  FETCH_SUCCESS,
  FETCH_FAILURE,
  SELECT_OPEN,
  SELECT_CLOSE,
  MRP_NAME_UPDATE,
  MRP_DATE_UPDATE,
  IVL_KEY_UPDATE,
  IVL_YEAR_UPDATE,
  SHOW_TECHPORT,
  SHOW_RESULT
} from "./action_types";

//  APOD  ////////////////////////////////////////////////////

export function getApod() {
  return function(dispatch) {
    fetch("http://localhost:3000/apod")
      .then(response => response.json())
      .then(data => {
        const received = {
          artist: data.copyright,
          title: data.title,
          date: data.date,
          explanation: data.explanation,
          link: data.url
        };
        dispatch(fetchSuccess());
        dispatch(showResult(received));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

//  MRP  ////////////////////////////////////////////////////

export function onOpen() {
  return function(dispatch) {
    dispatch({
      type: SELECT_OPEN,
      payload: true
    });
  };
}
export function onClose() {
  return function(dispatch) {
    dispatch({
      type: SELECT_CLOSE,
      payload: false
    });
  };
}

export function mrpNameUpdate(name) {
  return function(dispatch) {
    dispatch({
      type: MRP_NAME_UPDATE,
      payload: name
    });
  };
}

export function mrpDateUpdate(date) {
  return function(dispatch) {
    dispatch({
      type: MRP_DATE_UPDATE,
      payload: date
    });
  };
}

export function getMrp() {
  return function(dispatch, getState) {
    const state = getState().FormReducer.mrpForm;
    const sent = {
      name: state.name,
      date: state.date
    };
    const endpoint = `https://api.nasa.gov/mars-photos/api/v1/rovers/${
      sent.name
    }/photos?earth_date=${
      sent.date
    }&api_key=1bLpXwhcARlJOhP0k9rQ0PBxWtO7JJRflDIeg16p`;

    fetch(endpoint)
      .then(response => response.json())
      .then(data => {
        const received = {
          name: data.photos.map(e => e.rover.name),
          earth_date: data.photos.map(e => e.earth_date),
          photos: data.photos
        };
        console.log(received);
        dispatch(fetchSuccess());
        dispatch(showResult(received));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

//  IVL  ////////////////////////////////////////////////////

export function ivlKeyUpdate(keyword) {
  return function(dispatch) {
    dispatch({
      type: IVL_KEY_UPDATE,
      payload: keyword
    });
  };
}

export function ivlYearUpdate(year) {
  return function(dispatch) {
    dispatch({
      type: IVL_YEAR_UPDATE,
      payload: year
    });
  };
}

export function getIvl() {
  return function(dispatch, getState) {
    const state = getState().FormReducer.ivlForm;
    const sent = {
      keyword: state.keyword,
      year: state.year
    };
    const endpoint = `https://images-api.nasa.gov/search?q=${sent.keyword}%${
      sent.year
    }`;

    fetch(endpoint)
      .then(response => response.json())
      .then(data => {
        const received = {
          info: data.collection.items.map(i => i)
        };
        console.log(received);
        dispatch(fetchSuccess());
        dispatch(showResult(received));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

//  TECHPORT  ////////////////////////////////////////////////////

export function getTechport() {
  return function(dispatch) {
    fetch(
      "https://api.nasa.gov/techport/api/projects?api_key=1bLpXwhcARlJOhP0k9rQ0PBxWtO7JJRflDIeg16p"
    )
      .then(response => response.json())
      .then(data => {
        const received = {
          id: data.projects.projects.map(i => i.id),
          lastUpdated: data.projects.projects.map(i => i.lastUpdated)
        };
        console.log(received);
        dispatch(fetchSuccess());
        dispatch(getTechport2(received));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

export function getTechport2(received) {
  return function(dispatch) {
    const sent = {
      id: received.id.map(i => i),
      year: state.year
    };
    fetch(
      `https://api.nasa.gov/techport/api/projects/${received.id.map(
        i => i
      )}?updatedSince=${received.lastUpdated.map(
        i => i
      )}&api_key=1bLpXwhcARlJOhP0k9rQ0PBxWtO7JJRflDIeg16p`
    )
      .then(response => response.json())
      .then(data => {
        const received2 = {
          benefits: data.project.benefits
        };
        dispatch(fetchSuccess());
        dispatch(showTechport(received2));
      })
      .catch(error => {
        dispatch(fetchFailure());
        console.error("Error:", error);
      });
  };
}

export function showTechport(received2) {
  return function(dispatch) {
    dispatch({
      type: SHOW_TECHPORT,
      payload: true,
      receivedinfo: received2
    });
  };
}

