import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getTechport } from "../actions/formActions/actions";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3.5vmax",
    position: "relative",
    bottom: "70px",
    marginBottom: "5rem"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "80px"
  },
  text: {
    color: "#30323e",
    fontFamily: "Quicksand",
    backgroundColor: "rgba(216, 220, 242, 0.6)",
    fontWeight: "bold",
    padding: 30,
    marginTop: 50
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "3vmax",
    position: "relative",
    bottom: "100px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button1: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb",
    marginBottom: 70,
    margin: 50
  },
  button2: {
    position: "relative",
    top: 50,
    marginBottom: 70,
    margin: 50
  },
  about: {
    fontFamily: "Righteous"
  }
};
class TechportResult extends Component {
  componentDidMount() {
    this.props.dispatch(getTechport());
  }
  render() {
    const { classes } = this.props;
    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>

        <div className={classes.all}>
          <div className={classes.body}>
            <div>
              <h1 className={classes.header}>
                {this.props.receivedInfo.title}
              </h1>
              <h3 className={classes.info}>
                Work Location: {this.props.receivedInfo.workLocation}
              </h3>
              <h3 className={classes.info}>
                End Date: {this.props.receivedInfo.endDate}
              </h3>
            </div>
            <div className={classes.grid}>
              <img
                className={classes.picture}
                src={this.props.receivedInfo.img_src}
                alt=""
              />
              <div className={classes.text}>
                <h3 className={classes.about}>About the Project</h3>
                <p>{this.props.receivedInfo.description}</p>
              </div>
              <div className={classes.text}>
                <h3 className={classes.about}>Benefits</h3>
                <p>{this.props.receivedInfo.benefits}</p>
              </div>
            </div>
            <div />
            <Button
              className={classes.button1}
              variant="contained"
              color="primary"
              href="techport"
            >
              Show Another Project
            </Button>
            <Button
              className={classes.button2}
              variant="contained"
              color="primary"
              href="/"
            >
              Go Back
            </Button>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    receivedInfo: state.receivedInfo
  };
};

TechportResult.propTypes = {
  classes: PropTypes.object.isRequired
};

const withstyles = withStyles(styles)(TechportResult);
const techportResultConnected = connect(mapStateToProps)(withstyles);
export default techportResultConnected;
