import React, { Component } from "react";
import { connect } from "react-redux";
import Results from "./Results";
import Main from "./Main";
import { reset } from "../Actions/Actions";

class Status extends Component {
  render() {
    let renderForm;

    if (!this.props.fetchStart) {
      renderForm = <Main />;
    }

    if (this.props.loading) {
      renderForm = (
        <Main
          title={this.props.loadingContent.title}
          content={this.props.loadingContent.content}
        />
      );
    }

    if (this.props.success) {
      renderForm = (
        <Results
          onReset={() => {
            this.props.dispatch(reset());
          }}
        />
      );
    }

    if (this.props.failure) {
      renderForm = <Results />;
    }

    return <div>{renderForm}</div>;
  }
}

const mapStateToProps = state => {
  return {
    submitted: state.isSubmitted,
    loading: state.isLoading,
    success: state.isSuccess,
    failure: state.isFailure,
    loadingContent: state.loadingContent,
    reset: state.reset
  };
};

const connectedThree = connect(mapStateToProps)(Status);
export default connectedThree;
