import React, { Component } from "react";
import "./App.css";
import SelectForm from "./container/selectForm";
import Result from "./components/results";
import { connect } from "react-redux";
import Main from "./components/main";

export class App extends Component {
  render() {
    let pageRendered;
    if (this.props.showResults) {
      pageRendered = (
        <div>
          <Main /> <Result />
        </div>
      );
    } else if (this.props.loading) {
      pageRendered = (
        <Main
          title={this.props.loadingContent.title}
          content={this.props.loadingContent.content}
        />
      );
    } else {
      pageRendered = (
        <div>
          <Main /> <SelectForm />
        </div>
      );
    }

    return <div>{pageRendered}</div>;
  }
}

//APP component'ı state'e bağlansın; render etttiği selectForm ya da Result'a, gerekli parametreleri prop olarak göndersin.

const mapStateToProps = state => {
  return {
    showResults: state.form.showResults,
    loading: state.isLoading,
    loadingContent: state.loadingContent,
    failureContent: state.failureContent
  };
};

const AppConnected = connect(mapStateToProps)(App);
export default AppConnected;
