import React, { Component } from "react";
import { connect } from "react-redux";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import { Animated } from "react-animated-css";
import Toolbar from "@material-ui/core/Toolbar";
import MrpList from "./mrpList";

const styles = {
  header: {
    color: "#E040FB",
    fontFamily: "Righteous",
    fontWeight: "bold",
    fontSize: "3rem",
    position: "relative",
    bottom: "70px"
  },
  grid: {
    display: "flex",
    flexDirection: "column",
    justifyItems: "center",
    position: "relative",
    bottom: "60px"
  },
  home: {
    color: "#d8dcf2",
    fontSize: "2rem",
    fontFamily: "Lobster, cursive",
    textDecoration: "underline",
    textDecorationColor: "#e040fb"
  },
  body: {
    position: "relative",
    top: 100
  },
  all: {
    backgroundColor: "#212127",
    borderRadius: "5%",
    margin: 30
  },
  info: {
    color: "#d8dcf2",
    fontFamily: "Quicksand",
    fontWeight: "bold",
    fontSize: "30px",
    position: "relative",
    bottom: "70px"
  },
  picture: {
    maxWidth: "100%",
    maxHeight: "100vh",
    margin: "auto"
  },
  button: {
    position: "relative",
    top: 50,
    backgroundColor: "#e040fb"
  },
  date: {
    color: "#E040FB"
  }
};

class MrpResult extends Component {
  render() {
    const { classes } = this.props;

    /*
    let nameShow = props.receivedInfo.name.map(name => (
      <h1 className={classes.header}>{name}</h1>
    ));
    let dateShow = props.receivedInfo.earth_date.map(date => (
      <h3 className={classes.info}>Date: {date}</h3>
    ));
    let cameraShow = props.receivedInfo.camera.map(camera => (
      <h3 className={classes.info}>Camera: {camera}</h3>
    ));
    let imgShow = props.receivedInfo.img_src.map(image => (
      <img className={classes.picture} src={image} alt="" />
    ));


    for (let i = 0; i < receivedInfo.name.length; i++) {
      return <MrpList receivedInfo.name[i] />
    }

    for (let i = 0; i < receivedInfo.earth_date.length; i++) {
      dateShow = receivedInfo.earth_date[i];
    }

    for (let i = 0; i < receivedInfo.camera.length; i++) {
      cameraShow = receivedInfo.camera[i];
    }

    for (let i = 0; i < receivedInfo.img_src.length; i++) {
      imgShow = receivedInfo.img_src[i];
    }
