    return (
      <React.Fragment>
        <Toolbar>
          <Animated
            animationIn="fadeInRight"
            animationOut="fadeOut"
            isVisible={true}
          >
            <Button className={classes.home} href="/">
              Universa Localis
            </Button>
          </Animated>
        </Toolbar>
        <div className={classes.all}>
          {this.props.receivedInfo.name.map(name => (
            <MrpList name={name} />
          ))}

          {this.props.receivedInfo.earth_date.map(date => (
            <MrpList name={date} />
          ))}
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            style={{ marginBottom: 70 }}
            href="/mrp"
          >
            Go Back
          </Button>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  state = state.FormReducer;
  return {
    receivedInfo: state.receivedInfo
  };
};

const withstyles = withStyles(styles)(MrpResult);
const mrpResultConnected = connect(mapStateToProps)(withstyles);
export default mrpResultConnected;
